scripts.prebuild:
node -e "const fs = require('fs'); const { execSync } = require('child_process'); const pkg = JSON.parse(fs.readFileSync('package.json', 'utf8')); let buildNumber = '0', gitHash = 'dev'; try { buildNumber = execSync('git rev-list --count HEAD').toString().trim(); } catch {} try { gitHash = execSync('git rev-parse --short HEAD').toString().trim(); } catch {} fs.writeFileSync('src/buildInfo.ts', 'export const VERSION = '' + pkg.version + '';
export const BUILD_NUMBER = ' + buildNumber + ';
export const BUILD_HASH = '' + gitHash + '';
');"
